<?phpnamespace config;class connection{    private string $hostName = 'localhost';    private string $userName = 'root';    private string $pass = '';    private string $DbName = 'forgot_password';    private \mysqli $conn;    // Constructor para sa auto-connect    public function __construct()    {        $this->conn = new \mysqli($this->hostName, $this->userName, $this->pass, $this->DbName);        if ($this->conn->connect_error) {            die('Connection Problem: ' . $this->conn->connect_error);        }    }    public function Connect(): \mysqli    {        return $this->conn;    }}