<?phpnamespace Services;//Import PHPMailer classes into the global namespace//These must be at the top of your script, not inside a functionuse PHPMailer\PHPMailer\Exception;use PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\SMTP;//Load Composer's autoloaderinclude_once '../../vendor/autoload.php';class Mailer{    public static function SendMail($recipient,$code):void    {        //Create an instance; passing `true` enables exceptions        $mail = new PHPMailer(true);        try {            //Server settings            $mail->SMTPDebug = SMTP::DEBUG_SERVER;                      //Enable verbose debug output            $mail->isSMTP();                                            //Send using SMTP            $mail->Host       = 'smtp.gmail.com';                     //Set the SMTP server to send through            $mail->SMTPAuth   = true;                                   //Enable SMTP authentication            $mail->Username   = 'jeromvictorio18@gmail.com';                     //SMTP username            $mail->Password   = 'kapb ysmf zgcq sive';                                       //SMTP password            $mail->SMTPSecure = PHPMailer::ENCRYPTION_SMTPS;            //Enable implicit TLS encryption            $mail->Port       = 465;                                    //TCP port to connect to; use 587 if you have set `SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS`            //Recipients            $mail->setFrom('victoriomark54@gmail.com', 'Forgot Password');            $mail->addAddress($recipient,'testName');            //Content            $mail->isHTML(true);                                  //Set email format to HTML            $mail->Subject = 'Password Reset Code';            $mail->Body    = 'Your password reset code: <strong>'.$code.'</strong>';            $mail->AltBody = 'Please dont reply this email';            $mail->SMTPDebug = 0; // I-disable ang debug output            $mail->send();            session_start();            $message = 'We sent a password reset otp to your email: <br> <strong>'.$recipient.'</strong>';            $_SESSION['otp_response'] = $message;            $_SESSION['email'] = $recipient;            echo json_encode(['success' => true]);        } catch (Exception) {            echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";        }    }}