<?phpnamespace conrollers;use model\forgot_password;use Services\Mailer;include_once '../model/forgot_password.php';include_once '../Services/Mailer.php';class forgot_passController{public static function forgotPass(){    $email = htmlspecialchars($_POST['Email']);    if (empty($email)){        echo 'Email is requred';        return;    }    $checker = new forgot_password();    // checker email     if (!$checker::checkIfEmailIsExist($email)){         echo json_encode(['success' => false, 'message' => 'Email is not exist']);         return;     }    $code = random_int(222,555500); // Generate OTP    $SendEmail = new Mailer();    $SendEmail::SendMail($email,$code);    $checker::storeOTP($code,$email);}public static function verifyOtp(){    $email = htmlspecialchars($_POST['email']);    $opt = htmlspecialchars($_POST['opt_code']);    if (empty($email) || empty($opt)){        echo 'email and otp is required';        return;    }    $verify = new forgot_password();    if ($verify::verifyOPT($opt,$email)){        $verify::storeOTP('0',$email);        echo json_encode(['success' => true]);    }else{       echo json_encode(['success' => false, 'message' => 'you entered wrong code']);    }}}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $checker = new forgot_passController();    match ($_POST['action']){        'checker' => $checker::forgotPass(),         'verify' => $checker::verifyOtp(),        default => http_response_code(400)    };}