<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <title>Change Password</title></head><style>    body{        height: 100vh;        width: 100%;        display: flex;        flex-direction: column;        justify-content: center;        align-items: center;    }</style><body><form id="change-pass-form" class="card col-lg-3 p-3">    <h3 class=" fw-bold  mb-3 text-secondary">New Password</h3>    <div id="message_alert" class="alert alert-success">      please create new password    </div>    <?php    session_start();    $email = $_SESSION['email'];    if (!$email){        header('Location: ../../index.php');    }    ?>    <input name="email" type="hidden" value="<?= $_SESSION['email'] ?>">    <div class="form-floating mb-3">        <input type="password" name="newPass" class="form-control" id="newPass" placeholder="create new password">        <label for="newPass">Create new password</label>    </div>    <div class="form-floating  mb-2">        <input type="password" class="form-control" id="ConfirmPassword" placeholder="ConfirmPassword">        <label for="ConfirmPassword">Confirm password</label>        <div id="error_message" class="invalid-feedback">cvcvcb</div>    </div>    <button id="btn_submit" type="submit" class="btn btn-primary">Change</button></form></body></html><script>$(document).ready(function (){    $(document).on('submit','#change-pass-form',function (event){        event.preventDefault()        const DataForm = new FormData(this)        DataForm.append('action','change')        $.ajax({            url: '../conrollers/forgot_passController.php',            type: 'post',            data: DataForm,            dataType: 'json',            contentType: false,            processData: false,            success: function (res){                console.log(res)                if (res.success == true){                    $('#message_alert').html(res.message)                }else {                    $('#message_alert').addClass('alert-danger')                }            }        })    })})/// for confirm passwordfunction confirmPassoword(){    const newPass = document.getElementById('newPass');    const confirmPass = document.getElementById('ConfirmPassword');    const error_message = document.getElementById('error_message');    const btn_submit = document.getElementById('btn_submit');    document.getElementById('ConfirmPassword').addEventListener('keyup',()=>{        if (newPass.value !== confirmPass.value){           confirmPass.classList.add('is-invalid')            error_message.innerText = 'Passwords do not match. Please try again.'            btn_submit.disabled = true        }else {            confirmPass.classList.remove('is-invalid')            btn_submit.disabled = false        }    })}confirmPassoword()</script>