<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <title>Forgot Password</title></head><style>    body{        height: 100vh;        width: 100%;        display: flex;        flex-direction: column;        justify-content: center;        align-items: center;    }</style><body><form id="forgot_pass_form" class="card col-lg-3 p-3">     <div class="mb-1 text-center">         <h3 class=" fw-bold text-secondary ">Forgot Password</h3>         <span class="text-center">Enter your Email</span>     </div>      <div id="alert_con"></div>    <div class="form-floating mb-3">        <input type="email" name="Email" class="form-control" id="email" placeholder="name@example.com">        <label for="email">Email address</label>    </div>    <button id="btn_submit" type="submit" class="btn btn-primary">Continue</button></form></body></html><script>    $(document).ready(function (){        $(document).on('submit','#forgot_pass_form',function (event){            event.preventDefault()            const Dataform = new FormData(this)            Dataform.append('action','checker')            // load the loader            $('#btn_submit').html(                `                        <div class="spinner-border spinner-border-sm" role="status">                          <span class="visually-hidden">Loading...</span>                        </div>                        `            );            $.ajax({                url: '../conrollers/forgot_passController.php',                data: Dataform,                type: 'post',                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.success === true){                           window.location.href = 'verification-code.php'                    }                }            })        })    })</script>