<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <title>Verification Code</title></head><style>    body{        height: 100vh;        width: 100%;        display: flex;        flex-direction: column;        justify-content: center;        align-items: center;    }</style><body><form id="verify_form" class="card col-lg-3 p-3">    <div class="mb-3 text-center">        <h3 class=" fw-bold text-primary">Verification Code</h3>    </div>    <div id="alert_message" class="alert alert-success">        <?php        session_start();        echo $_SESSION['otp_response'];        ?>    </div>      <input name="email" type="hidden" value="<?=$_SESSION['email'] ?>">    <div class="form-floating mb-3">        <input type="number" name="opt_code" class="form-control" id="OTP" placeholder="Enter Code">        <label for="OTP">Enter Code</label>    </div>    <button type="submit" class="btn btn-primary">Submit</button></form></body></html><script>    $(document).ready(function (){        $(document).on('submit','#verify_form',function (event){            event.preventDefault()            const Dataform = new FormData(this)            Dataform.append('action','verify')            $.ajax({                url: '../conrollers/forgot_passController.php',                data: Dataform,                type: 'post',                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.success === true){                        window.location.href = 'create-new-password.php';                    }else {                        $('#alert_message').html(                         res.message                        );                        $('#alert_message').addClass('alert-danger')                    }                }            })        })    })</script>